local string = require("string")
local io = require("io")
local table = require("table")

function shuffle(t)
    if type(t)~="table" then
        return
    end
    local tab={}
    local index=1
    while #t~=0 do
        local n = math.random(0, #t)
        if t[n] ~=nil then
            tab[index] = t[n]
            table.remove(t, n)
            index = index + 1
        end
    end
    return tab
end


-- 四舍五入
function round_four(n)
    local v1, v2 = math.modf(n)
    if n > 0 then
        if v2 >= 0.5 then
            v1 = v1 + 1
        end
    else
        if v2 < -0.5 then
            v1 = v1 - 1
        end
    end
    return v1
end

function dateTime(now)
    if now ~= nil then
        return os.date("%Y-%m-%d %H:%M:%S", now)
    else
        return os.date("%Y-%m-%d %H:%M:%S", os.date())
    end
end

-- 今天
function today()
    return os.time()
end

-- 昨天
function yesterday(now)
    local t = os.date("*t", now)
    t.day = t.day - 1
    -- t.hour = 0
    -- t.min = 0
    -- t.sec = 0
    return os.time(t)
end

-- 前天
function before_yesterday(now)
    local t = os.date("*t", now)
    t.day = t.day - 2
    -- t.hour = 0
    -- t.min = 0
    -- t.sec = 0
    return os.time(t)
end

-- 明天
function tomorrow(now)
    local t = os.date("*t", now)
    t.day = t.day + 1
    -- t.hour = 0
    -- t.min = 0
    -- t.sec = 0
